        try{
            const matTypeRes = await axios.get(baseURL)
            setMatTypeData(matTypeRes.data)
        }
        catch(error) {
            console.log(error," sdfdsfdsddfss")
        }
    }

    
    useEffect(() => {
        fetchApi()
    },[])

    return (
        <View flex={1}>
            <FlatList 
                data={matTypeData}
                keyExtractor={item => `${item.Mtart}`}
                renderItem={ListViewType}>
            </FlatList>
        </View>
    );
}

export default MaterialType;

const styles = StyleSheet.create({
    flatListItem:{
        backgroundColor:"#fff",
        borderRadius:10,
        padding:10,
        margin:4
    }
})
